    <script>
    const services = document.querySelector('.services');
    const servicesHeader = document.querySelector('.services-header');

    services.addEventListener('mouseover', () => {
      servicesHeader.style.display = 'flex';
    });

    services.addEventListener('mouseout', () => {
      servicesHeader.style.display = 'none';
    });

    servicesHeader.addEventListener('mouseover', () => {
      servicesHeader.style.display = 'flex';
    });

    servicesHeader.addEventListener('mouseout', () => {
      servicesHeader.style.display = 'none';
    });

    function closeHeaderMenu() {
      document.querySelector('.header-bottom-menu').style.display = 'none'
    }
    function openHeaderMenu() {
      document.querySelector('.header-bottom-menu').style.display = 'flex'
    }
    </script>
